mapper.select:
select o.o_id, o.o_no, o.o_com_no, o.o_count, o.o_custom_name, o.o_contacts, o.o_tel, o.o_create_date, o.o_status, o.o_pay, o.o_note, o.o_product_code, o.o_indeed_count, p.pro_price, p.pro_name from orders o left join product p on o.o_product_code=p.pro_code
o.o_custom_name=#{custom_name}
o.o_salesman=#{salesman}
o.o_no=#{oNo}
order by o.o_create_date DESC

select o.o_id, o.o_no, o.o_com_no, o.o_count, o.o_custom_name, o.o_contacts, o.o_tel, o.o_create_date, o.o_status, o.o_pay, o.o_note, o.o_product_code, o.o_indeed_count, p.pro_price, p.pro_name from orders o left join product p on o.o_product_code=p.pro_code
and o.o_create_date=#{oCreateDate}
and o.o_no=#{oNo}
and o.o_status=#{oStatus}
order by o.o_create_date DESC
